<style>*{-ms-box-sizing:border-box;-moz-box-sizing:border-box;-webkit-box-sizing:border-box;box-sizing:border-box}:after,:before{-ms-box-sizing:border-box;-moz-box-sizing:border-box;-webkit-box-sizing:border-box;box-sizing:border-box}body{margin:0;font-size:16px;font-family:Roboto,sans-serif;line-height:1.5}.content-wrapper{width:100%;margin:auto;padding:0 15px}@media (max-width:1439px){.content-wrapper{width:100%}}@media (min-width:1440px){.content-wrapper{width:1440px}}.container{max-width:100%;position:relative;margin:auto;padding:0 60px}@media (max-width:1399px){.container{max-width:100%}}@media (min-width:1400px){.container{max-width:1400px}}@media (max-width:767px){.container{padding:0 15px}}.content{min-height:40vh}.kicker{font-size:16px;font-weight:700;color:#000;position:relative;padding-left:80px}.kicker:before{content:"";width:70px;height:2px;position:absolute;left:0;top:10px;background:#000}h2{font-size:41px;font-weight:700;line-height:1;margin-bottom:20px}</style>